import React, {useEffect, useContext, useState} from 'react';
import {NavLink, Redirect} from 'react-router-dom'

import useFetch from 'hooks/useFetch'
// import Loading from 'components/loading'
// import TagList from 'components/tagList'
// import ErrorMessage from 'components/errorMessage'
// import {CurrentUserContext} from 'contexts/currentUser'

const UserProfile = ({location, match }) => {
    const slug = match.params.slug
    const isFavorites = location.pathname.includes('favorites')
    const apiUrl = `/profiles/${slug}`
    const [{response}, doFetch] = useFetch(apiUrl)

    useEffect(()=> {
        doFetch()
    }, [doFetch])

    if (!response) {
        return null
    }

    return (
        <div className='profile-page'>
            <div className="user-info">
                <div className="container">
                    <div className="row">
                        <div className="col-xs-12 col-md-10 offset-md-1">
                            <img className='user-img' src={response.profile.image} alt=""/>
                            <h4>{response.profile.username}</h4>
                            <p>{response.profile.bio}</p>
                        </div>
                    </div>
                </div>
            </div>
            <div className="container">
                <div className="row">
                    <div className="col-xs-12 col-md-10 offset-md-1">
                        <div className="articles-toggle">
                            <ul className="nav nav-pills outline-active">
                                <li className="nav-item">
                                    <NavLink className='nav-link' to={`/profiles/${response.profile.username}`} exact>
                                        My Posts
                                    </NavLink>
                                </li>
                                <li className="nav-item">
                                    <NavLink className='nav-link' to={`/profiles/${response.profile.username}/favorites`}>
                                        Favorites Posts
                                    </NavLink>
                                </li>
                            </ul>
                        </div>
                        User Articles
                    </div>
                </div>
            </div>
        </div>
    )
}

export default UserProfile;